import React, { useState } from 'react';
import StadiumImage from "../../assets/img/logo.png";
import styled from 'styled-components';

const InfoBlock = styled.div`
width: 90%;
height: 200px;

background:#F8F6F4;
border-top: 1px solid #000000;
border-bottom: 1px solid #000000;

position: relative;

margin: 0 auto;
padding-left: 50px;

margin-top: 32px;
display: flex;
align-items: center;
font-size: 16px
`

const Image = styled.img`
width: 160px;
height: 120px;
float: left;
margin-right: 20px;
`

const Content = styled.div`
flex-direction: column;
`

function InformationPage({ name, address, image}) {
  return <InfoBlock>
    <div>
        <Image src={StadiumImage} alt="stadium image" />
            <h2>{name}</h2>
            <p>{address}</p>
    </div>
  </InfoBlock>
}

const dummyData = [
    {
      name: "Stadium 1",
      address: "Address 1",
      image: {StadiumImage}
    }
];

function StadiumDetail() {
    return (
        <div>
            {dummyData
            .map((data, index) =>
            <InformationPage
            key = {index}
            name={data.name}
            address={data.address}
            image={data.image}
            />
            )}
        </div>
    )
}

export default StadiumDetail;
