import React from 'react';
import styled from 'styled-components';

// 컴포넌트 스타일 정의
const DateBlock = styled.div`
  margin: 10px;
  margin-right: 50px;
  text-align: center;
`;

const TimeBlock = styled.div`
  display: inline-block;
  width: 30px;
  height: 30px;
  margin: 5px;
  border: 1px solid #000;
`;

// 오늘 날짜부터 일주일 동안의 날짜를 가져오는 함수
const getDatesForWeek = () => {
  const dates = [];
  for (let i = 0; i < 7; i++) {
    const date = new Date();
    date.setDate(date.getDate() + i);
    dates.push(date);
  }
  return dates;
}

function ReservationPage() {
  const dates = getDatesForWeek();
  
  return (
    <div>
      {dates.map((date, index) => (
        <DateBlock key={index}>
          {date.toLocaleDateString()} {/* 변경 가능: 원하는 날짜 형식으로 수정 */}
          {[...Array(8)].map((_, i) => <TimeBlock key={i} />)} {/* 8개의 블록 생성 */}
        </DateBlock>
      ))}
    </div>
  );
};

export default ReservationPage;
