import React from 'react';

function FacilityPage({ stadiums }) {
  const itemsPerPage = 7; // 한 페이지에 표시할 체육 시설 수

  // 현재 페이지에서 표시해야 할 체육 시설들을 계산하는 함수
  const getCurrentPageItems = (stadiums, currentPage) => {
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    return stadiums.slice(startIndex, endIndex);
  };

  return (
    <div>
      <h1>체육 시설 목록</h1>

      {/* 현재 페이지에서 표시할 체육 시설 블록 생성 */}
      {getCurrentPageItems(stadiums, 1).map((stadium, index) => (
        <div key={index}>
          {/* 체육 시설 블록 내용 */}
          <h3>{stadium.name}</h3>
          <p>{stadium.description}</p>
        </div>
      ))}

      {/* 페이지 번호 등 추가 UI 요소 */}
    </div>
  );
}

export default FacilityPage;
