import React, { useEffect, useState } from "react";
import { useSelector } from "react-redux";
import { styled } from "styled-components";

const HeaderUser = () => {
    const user = useSelector((state) => state.auth.user);
    const [username, setUsername] = useState("");

    useEffect(() => {
        const fetchUserProfile = async () => {
            try {
                if (user && user.accessToken) {
                    console.log("hello fetchUserProfile", user.accessToken, user.username, user.user_id);
                    const response = await fetch(
                        `http://localhost:8000/users/${user.username}/profile`,
                        {
                            headers: {
                                Authorization: `Bearer ${user.accessToken}`,
                            },
                        }
                    );
                    console.log(user);
                    if (response.ok) {
                        const data = await response.json();
                        setUsername(data.username);
                        setPhone(data.phone_number);
                        setIsManager(data.is_manager);
                        setPoints(data.points);
                    } else {
                        console.error("사용자 정보 요청 실패");
                    }
                }
            } catch (error) {
                console.error("사용자 정보 요청 에러:", error);
            }
        };

        fetchUserProfile();
    }, [user]);

    return (
        <>
            <ToMyPage>
                <div>My Page {user.username}</div>
            </ToMyPage>
        </>
    );
};

export default HeaderUser;

const ToMyPage = styled.div`
    font-weight: 700;
    font-size: 2rem;
    color: brown;
    margin: auto;
`;
