import React, { useState } from 'react';
import styled from 'styled-components';
import { useLocation } from 'react-router-dom';

const PaymentContainer = styled.div`
  width: 50%;
  height: auto;
  background: #F8F6F4;
  border-top: 1px solid #000000;
  border-bottom: 1px solid #000000;
  margin: auto;
  padding: 50px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  font-size: 18px;
`;

const PaymentText = styled.h2`
  margin-bottom: 20px;
  font-size: 40px;
`;

const PointContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  grid-gap: 20px;
  margin-bottom: 20px;
`;

const PointBlock = styled.div`
  width: 100px;
  height: 100px;
  background-color: #19204E;
  color: white;
  display: flex;
  justify-content: center;
  align-items: center;
  font-size: 24px;
  cursor: pointer;
`;

const InputContainer = styled.div`
  display: flex;
  align-items: center;
  margin-bottom: 20px;
`;

const PointInput = styled.input`
  width: 100px;
  height: 40px;
  border: 1px solid #000000;
  border-radius: 4px;
  padding: 8px;
  margin-right: 10px;
`;

const ChargeButton = styled.button`
  width: 240px;
  background-color: #19204E;
  border: none;
  color: white;
  padding: 15px 32px;
  text-align: center;
  text-decoration: none;
  display: inline-block;
  font-size: 16px;
  cursor: pointer;
`;

const PaymentPage = () => {
  const location = useLocation();
  const { requiredPoints } = location.state || {};
  const [inputPoints, setInputPoints] = useState('');

  const handleCharge = () => {
    // Add your charge logic here
  };

  return (
    <PaymentContainer>
      <PaymentText>충전 정보</PaymentText>
      <PointContainer>
        {[10, 20, 30, 50, 100, 200].map((points) => (
          <PointBlock key={points}>{points} 포인트</PointBlock>
        ))}
      </PointContainer>
      <InputContainer>
        <PointInput
          type="number"
          placeholder="직접 입력"
          value={inputPoints}
          onChange={(e) => setInputPoints(e.target.value)}
        />
        <span>포인트</span>
      </InputContainer>
      <ChargeButton onClick={handleCharge}>충전하기</ChargeButton>
    </PaymentContainer>
  );
};

export default PaymentPage;
