import React, { useState } from 'react';
import 'react-dates/initialize';
import 'react-dates/lib/css/_datepicker.css';
import { DatePicker } from 'react-dates';
import styled from 'styled-components';
import moment from 'moment';

const PageWrapper = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: flex-start;;
  height: 100vh;
  margin-top: 50px;
`;

const DateBlock = styled.div`
  display: flex;
  align-items: center;
  margin: 10px;
  text-align: center;
`;

const TimeBlock = styled.div`
  padding: 5px;
  margin: 5px;
  border: 1px solid #000000;
  display: flex;
  align-items: center;
  justify-content: center;
  text-align: center;
  cursor: pointer;
  white-space: nowrap;
  font-size: 12px;

  &:hover {
    background-color: #ddd;
  }
`;

function ReservationPage() {
    const [selectedDate, setSelectedDate] = useState(moment());
    const [focused, setFocused] = useState(false);
    const [selectedDates, setSelectedDates] = useState([]);
    const dates = getDatesForWeek();

    const getDatesForWeek = (date) => {
        const startOfWeek = moment(date).startOf('week');
        const dates = [];
        for (let i = 0; i < 7; i++) {
          dates.push(moment(startOfWeek).add(i, 'days'));
        }
        return dates;
      };

  const onDateChange = (date) => {
    setSelectedDate(date);
    const datesForWeek = getDatesForWeek(date);
    setSelectedDates(datesForWeek);
  };

    return (
        <div>
        <DatePicker
          date={selectedDate}
          onDateChange={onDateChange}
          focused={focused}
          onFocusChange={({ focused }) => setFocused(focused)}
          isOutsideRange={() => false}
          numberOfMonths={1}
        />
      <div>
        {selectedDates.map((date, index) => (
          <span key={index}>{date.format('YYYY-MM-DD')}</span>
        ))}
      </div>
    </div>
    );
};

export default ReservationPage;
