import React, { useState } from 'react';
import styled from 'styled-components';

const SearchBlock = styled.div`
width: 1024px;
height: 256px;

position: relative;
background: #EDEDED;

margin: 0 auto;
padding: 20px;

margin-top: 32px;
display: flex;
flex-direction: column;
`

function SearchPage() {
  const [sport, setSport] = useState('');
  const [region, setRegion] = useState('');
  const [searchResults, setSearchResults] = useState([]);

  const handleSearch = () => {
    // 종목과 지역에 따라 체육 시설을 검색하는 로직을 작성하세요.
    // 예를 들어, 서버와 통신하여 검색 결과를 가져오는 API 호출 등을 수행할 수 있습니다.
    // 가져온 검색 결과는 setSearchResults()를 통해 상태를 업데이트하세요.
    // setSearchResults([...newResults]); // 예시: 가져온 검색 결과를 상태에 업데이트

    // 여기서는 간단하게 검색 조건과 검색 결과를 콘솔에 출력하는 예시를 제공합니다.
    console.log('종목:', sport);
    console.log('지역:', region);
    console.log('검색 결과:', searchResults);
  };

  const handleClickRadioButton = (e) => {
    console.log(e.target.value)
    setSport(e.target.value)
  }

  return <SearchBlock>
    <div>
        종목
        &nbsp;&nbsp;&nbsp;&nbsp;
        <input
        type="radio"
        value="soccer"
        checked={sport ==="soccer"}
        onChange={handleClickRadioButton}
        />
        <label> 축구 </label>
        &nbsp;&nbsp;
        <input
        type="radio"
        value="basketball"
        checked={sport ==="basketball"}
        onChange={handleClickRadioButton}
        />
        <label> 농구 </label>
        &nbsp;&nbsp;
        <input
        type="radio"
        value="futsal"
        checked={sport ==="futsal"}
        onChange={handleClickRadioButton}
        />
        <label> 풋살 </label>

        <br/>
      <label>
        지역
        &nbsp;&nbsp;&nbsp;&nbsp;&nbsp;
        <select value={region} onChange={(e) => setRegion(e.target.value)}>
          <option value="">선택하세요</option>
          <option value="서울">서울</option>
          <option value="경기">경기</option>
          <option value="강원">강원</option>
          <option value="충청">충청</option>
          <option value="인천">인천</option>
        </select>
      </label>
      <br/>

      <button onClick={handleSearch}>검색</button>

      <hr/>
      <div>
        {searchResults.length > 0 ? (
          <ul>
            {searchResults.map((result, index) => (
              <li key={index}>{result}</li>
            ))}
          </ul>
        ) : (
          <p>검색 결과가 없습니다.</p>
        )}
      </div>
    </div>
  </SearchBlock>;
}

export default SearchPage;
