import React, { useState } from 'react';
import styled from 'styled-components';
import { useLocation } from 'react-router-dom';

const PaymentContainer = styled.div`
  width: 50%;
  height: auto;
  background: #F8F6F4;
  border-top: 1px solid #000000;
  border-bottom: 1px solid #000000;
  margin: auto;
  padding: 50px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  font-size: 18px;

  p {
    margin: 10px 0;
  }
`;

const CenteredWrapper = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 100vh;
`;

const PaymentText = styled.h2`
  margin-bottom: 20px;
  font-size: 40px;
`;

const PointsBlock = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  align-items: center;
  margin-bottom: 20px;
`;

const PointButton = styled.button`
  width: auto;
  height: 50px;
  margin: 5px;
  background-color: #19204E;
  border: none;
  color: white;
  cursor: pointer;
`;

const RechargeInput = styled.input`
  width: 100%;
  height: 50px;
  margin-bottom: 20px;
  padding: 10px;
`;

const RechargeButton = styled.button`
  width: 240px;
  background-color: #19204E;
  border: none;
  color: white;
  padding: 15px 32px;
  text-align: center;
  text-decoration: none;
  display: inline-block;
  font-size: 16px;
  cursor: pointer;
`;

const PaymentPage = () => {
  const location = useLocation();
  const { name, address, selectedDate, selectedTime, price, currentPoints, requiredPoints } =
    location.state || {};

  const [rechargePoints, setRechargePoints] = useState('');

  const handleRecharge = () => {
    console.log('Recharge button clicked. Points:', rechargePoints);
    // Add your recharge logic here
  };

  return (
    <CenteredWrapper>
      <PaymentContainer>
        <PaymentText>충전 정보</PaymentText>
        <p>필요 포인트: {requiredPoints}</p>
        <PointsBlock>
          {[10000, 30000, 50000, 60000, 80000, 100000].map((point) => (
            <PointButton key={point}>{point}</PointButton>
          ))}
        </PointsBlock>
        <RechargeInput
          type="number"
          value={rechargePoints}
          onChange={(e) => setRechargePoints(e.target.value)}
        />
        <RechargeButton onClick={handleRecharge}>충전하기</RechargeButton>
      </PaymentContainer>
    </CenteredWrapper>
  );
};

export default PaymentPage;
